catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    g_form.clearOptions('e_voucher_amount');
    if (newValue == 'e-commerce' || newValue == 'fooddelivery') {
        g_form.addOption('e_voucher_amount', 200, '200', 1);
    } else if (newValue == 'fooddelivery') {
        g_form.addOption('e_voucher_amount', 100, '100', 2);
    } else if (newValue == 'fooddelivery' || newValue == 'shopping') {
        g_form.addOption('e_voucher_amount', 400, '400', 3);
    } else if (newValue == 'e-commerce' || newValue == 'sportswear') {
        g_form.addOption('e_voucher_amount', 250, '250', 4);
    } else if (newValue == 'e-commerce' || newValue == 'sportswear' || newValue == 'shopping') {
        g_form.addOption('e_voucher_amount', 500, '500', 5);
    } else if (newValue == 'sportswear' || newValue == 'shopping') {
        g_form.addOption('e_voucher_amount', 750, '750', 6);
    } else {
        g_form.addOption('e_voucher_amount', 1000, '1000', 7);
    }



    //Type appropriate comment here, and begin script below

}
