catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var type = g_form.getValue('awards_appreciations');
    if (type == 'pob') {
        g_form.setValue('e_wallet_balance', '300', '300');
    } else if (type == 'osa') {
        g_form.setValue('e_wallet_balance', '400', '400');
    }else if (type == 'bem') {
        g_form.setValue('e_wallet_balance', '500', '500');
    }else if (type == 'sga') {
        g_form.setValue('e_wallet_balance', '250', '250');
    }else if (type == 'eha') {
        g_form.setValue('e_wallet_balance', '200', '200');
    }else if (type == 'bna') {
        g_form.setValue('e_wallet_balance', '600', '600');
    }else if (type == 'rsa') {
        g_form.setValue('e_wallet_balance', '800', '800');
    }else if (type == 'eea') {
        g_form.setValue('e_wallet_balance', '1000', '1000');
    }

    //Type appropriate comment here, and begin script below

}
