catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    //var type = g_from.getValue('e_voucher_type');
    g_form.clearOptions('e_voucher_brand');
    if (newValue == 'e-commerce') {
        g_form.clearOptions('e_voucher_brand');
        g_form.addOption('e_voucher_brand', 'flipkart', 'Flipkart', 1);
        g_form.addOption('e_voucher_brand', 'amazon ', 'Amazon ', 2);
        g_form.addOption('e_voucher_brand', 'myntra ', 'Myntra', 3);
        g_form.clearOptions('e_voucher_amount');
        g_form.addOption('e_voucher_amount', '200', '200', '1');
        g_form.addOption('e_voucher_amount', '250', '250', '2');
        g_form.addOption('e_voucher_amount', '500', '500', '3');
        g_form.addOption('e_voucher_amount', '1000', '1000', '4');

    } else if (newValue == 'shopping') {
        g_form.clearOptions('e_voucher_brand');
        g_form.addOption('e_voucher_brand', 'lifestyle', 'Lifestyle', 4);
        g_form.addOption('e_voucher_brand', 'allen solly ', 'Allen Solly', 5);
        g_form.addOption('e_voucher_brand', 'myntra ', 'Myntra', 6);
        g_form.clearOptions('e_voucher_amount');
        g_form.addOption('e_voucher_amount', '250', '250', '5');
        g_form.addOption('e_voucher_amount', '400', '400', '6');
        g_form.addOption('e_voucher_amount', '500', '500', '7');
        g_form.addOption('e_voucher_amount', '1000', '1000', '8');

    } else if (newValue == 'fooddelivery') {
        g_form.addOption('e_voucher_brand', 'zomato', 'Zomato', 7);
        g_form.addOption('e_voucher_brand', 'swiggy', 'Swiggy', 8);
        g_form.clearOptions('e_voucher_amount');
        g_form.addOption('e_voucher_amount', '100', '100', '9');
        g_form.addOption('e_voucher_amount', '200', '200', '10');
        g_form.addOption('e_voucher_amount', '400', '400', '11');
        g_form.addOption('e_voucher_amount', '1000', '1000', '12');
    } else if (newValue == 'sportswear') {
        g_form.addOption('e_voucher_brand', 'adidas', 'Adidas ', 9);
        g_form.addOption('e_voucher_brand', 'nike', 'Nike', 10);
        g_form.clearOptions('e_voucher_amount');
        g_form.addOption('e_voucher_amount', '250', '250', '13');
        g_form.addOption('e_voucher_amount', '500', '500', '14');
        g_form.addOption('e_voucher_amount', '750', '750', '15');
        g_form.addOption('e_voucher_amount', '1000', '1000', '16');

    }

    //Type appropriate comment here, and begin script below

}
