sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here
    var user = current.requested_for_employee;
    var gr = new GlideRecord('x_800355_corpora_0_employee');
    gr.addQuery('sys_id', user);
    gr.query();
    if (gr.next()) {
        template.print('<p><strong>Awards/Appreciations</strong>:' + gr.awards_appreciations + '</p>' + '<p><strong>E-Wallet Balance</strong>:' + gr.e_wallet_balance + '</p>');
    }


})(current, template, email, email_action, event);
