sys_script_include.script:
var ProjectAccount = Class.create();
ProjectAccount.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getAccount: function() {
        var value = this.getParameter("sysparm_user");
        //gs.info("PA" + sysid);
        var accountname = "";
        var gr = new GlideRecord('x_800355_corpora_0_employee');
        gs.info("Hello" + gr);
        gr.addQuery('sys_id', value);
        gr.query();
        if (gr.next()) {
            gs.info("hello" + gr.project_account);
            accountname = gr.getDisplayValue("project_account");
        }
        return accountname;
        //return accountname.getDisplayValue();
    },

    type: 'ProjectAccount'
});
