sys_script_include.script:
var GetUserByManager = Class.create();
GetUserByManager.prototype = {
    initialize: function() {},

    getUsersByManager: function() {
        var currentUser = gs.getUser();
        var Usergr = new GlideRecord('sys_user');
        gr.addQuery('manager', currentUser.getID());
        gr.query();
        var users = [];
        while (gr.next()) {
            while (userGr.next()) {
                var userData = {
                    id: userGr.getUniqueValue(),
                    name: userGr.getDisplayValue('name'),
                    email: userGr.getValue('email'),
                    department: userGr.getDisplayValue('department'),
                    phone: userGr.getValue('phone_number'),
                    manager: userGr.getDisplayValue('manager'),
                    isActive: userGr.getValue('active') == 'true'
                };
                users.push(userData);
            }

            return users;


        }

    },

    type: 'GetUserByManager'
};
